CREATE TABLE IF NOT EXISTS "frpm" ("CDSCode" VARCHAR, "Academic Year" VARCHAR, "County Code" VARCHAR, "District Code" INT, "School Code" VARCHAR, "County Name" VARCHAR, "District Name" VARCHAR, "School Name" VARCHAR, "District Type" VARCHAR, "School Type" VARCHAR, "Educational Option Type" VARCHAR, "NSLP Provision Status" VARCHAR, "Charter School (Y/N)" INT, "Charter School Number" VARCHAR, "Charter Funding Type" VARCHAR, "IRC" INT, "Low Grade" VARCHAR, "High Grade" VARCHAR, "Enrollment (K-12)" FLOAT, "Free Meal Count (K-12)" FLOAT, "Percent (%) Eligible Free (K-12)" FLOAT, "FRPM Count (K-12)" FLOAT, "Percent (%) Eligible FRPM (K-12)" FLOAT, "Enrollment (Ages 5-17)" FLOAT, "Free Meal Count (Ages 5-17)" FLOAT, "Percent (%) Eligible Free (Ages 5-17)" FLOAT, "FRPM Count (Ages 5-17)" FLOAT, "Percent (%) Eligible FRPM (Ages 5-17)" FLOAT, "2013-14 CALPADS Fall 1 Certification Status" INT, PRIMARY KEY ("CDSCode"));

CREATE TABLE IF NOT EXISTS "satscores" ("cds" VARCHAR, "rtype" VARCHAR, "sname" VARCHAR, "dname" VARCHAR, "cname" VARCHAR, "enroll12" INT, "NumTstTakr" INT, "AvgScrRead" INT, "AvgScrMath" INT, "AvgScrWrite" INT, "NumGE1500" INT, PRIMARY KEY ("cds"));

CREATE TABLE IF NOT EXISTS "schools" ("CDSCode" VARCHAR, "NCESDist" VARCHAR, "NCESSchool" VARCHAR, "StatusType" VARCHAR, "County" VARCHAR, "District" VARCHAR, "School" VARCHAR, "Street" VARCHAR, "StreetAbr" VARCHAR, "City" VARCHAR, "Zip" VARCHAR, "State" VARCHAR, "MailStreet" VARCHAR, "MailStrAbr" VARCHAR, "MailCity" VARCHAR, "MailZip" VARCHAR, "MailState" VARCHAR, "Phone" VARCHAR, "Ext" VARCHAR, "Website" VARCHAR, "OpenDate" DATE, "ClosedDate" DATE, "Charter" INT, "CharterNum" VARCHAR, "FundingType" VARCHAR, "DOC" VARCHAR, "DOCType" VARCHAR, "SOC" VARCHAR, "SOCType" VARCHAR, "EdOpsCode" VARCHAR, "EdOpsName" VARCHAR, "EILCode" VARCHAR, "EILName" VARCHAR, "GSoffered" VARCHAR, "GSserved" VARCHAR, "Virtual" VARCHAR, "Magnet" INT, "Latitude" FLOAT, "Longitude" FLOAT, "AdmFName1" VARCHAR, "AdmLName1" VARCHAR, "AdmEmail1" VARCHAR, "AdmFName2" VARCHAR, "AdmLName2" VARCHAR, "AdmEmail2" VARCHAR, "AdmFName3" VARCHAR, "AdmLName3" VARCHAR, "AdmEmail3" VARCHAR, "LastUpdate" DATE, PRIMARY KEY ("CDSCode"));

SELECT COUNT(T2.School) FROM frpm AS T1 INNER JOIN schools AS T2 ON T1.CDSCode = T2.CDSCode WHERE T2.County = 'Los Angeles' AND T2.Charter = 0 AND CAST(T1."Free Meal Count (K-12)" AS FLOAT) * 100 / T1."Enrollment (K-12)" < 0.18;

SELECT COUNT(DISTINCT S.CDSCode) FROM schools AS S INNER JOIN frpm AS F ON S.CDSCode = F.CDSCode WHERE S.Charter = 0 AND S.County = 'Los Angeles' AND (F."Free Meal Count (K-12)" * 100 / F."Enrollment (K-12)") < 0.18