SELECT SUM(t2.away_team_goal) FROM Player AS t1 INNER JOIN match AS t2 ON t1.player_api_id = t2.away_player_5 WHERE t1.player_name IN ('Daan Smith', 'Filipe Ferreira');

SELECT SUM(M.away_team_goal) FROM Match AS M JOIN Player AS P1 ON M.away_player_1 = P1.player_api_id JOIN Player AS P2 ON M.away_player_2 = P2.player_api_id JOIN Player AS P3 ON M.away_player_3 = P3.player_api_id JOIN Player AS P4 ON M.away_player_4 = P4.player_api_id JOIN Player AS P5 ON M.away_player_5 = P5.player_api_id JOIN Player AS P6 ON M.away_player_6 = P6.player_api_id JOIN Player AS P7 ON M.away_player_7 = P7.player_api_id JOIN Player AS P8 ON M.away_player_8 = P8.player_api_id JOIN Player AS P9 ON M.away_player_9 = P9.player_api_id JOIN Player AS P10 ON M.away_player_10 = P10.player_api_id JOIN Player AS P11 ON M.away_player_11 = P11.player_api_id WHERE P1.player_name = 'Daan Smith' OR P2.player_name = 'Daan Smith' OR P3.player_name = 'Daan Smith' OR P4.player_name = 'Daan Smith' OR P5.player_name = 'Daan Smith' OR P6.player_name = 'Daan Smith' OR P7.player_name = 'Daan Smith' OR P8.player_name = 'Daan Smith' OR P9.player_name = 'Daan Smith' OR P10.player_name = 'Daan Smith' OR P11.player_name = 'Daan Smith' OR P1.player_name = 'Filipe Ferreira' OR P2.player_name = 'Filipe Ferreira' OR P3.player_name = 'Filipe Ferreira' OR P4.player_name = 'Filipe Ferreira' OR P5.player_name = 'Filipe Ferreira' OR P6.player_name = 'Filipe Ferreira' OR P7.player_name = 'Filipe Ferreira' OR P8.player_name = 'Filipe Ferreira' OR P9.player_name = 'Filipe Ferreira' OR P10.player_name = 'Filipe Ferreira' OR P11.player_name = 'Filipe Ferreira'